customformpanel.beforeSave:

var year = container.find('form').find('[name=year]').val()
var month = container.find('form').find('[name=month]').val()
var birthday = container.find('form').find('[name=birthday]').val()

if((year=='' && month=='') && birthday==''){
    alert('请填写小孩子的年龄信息');
    return false;
}

if(birthday!=''){
    return true;
}

if(year==''){
    alert('请填写小孩子出生年份');
    return false;
}

if(month==''){
    alert('请选择小孩子出生月份');
    return false;
}

if(year.length!=4){
    alert('出生年份格式错误');
    return false;
}

return true;
